mapper.select:
{
	call BoardList (
		#{start, mode=IN,  jdbcType=INTEGER}
	   ,#{end,   mode=IN,  jdbcType=INTEGER}
	   ,#{board, mode=OUT, jdbcType=CURSOR,
	   					   javaType=java.sql.ResultSet,
	   					   resultMap = BoardList}
	)
}

	 select BOARD_NO, TITLE, content, EMP_NO, READ_COUNT, ref, RE_STEP, RE_LVL, IN_DATE 
        from (
            select rownum rn, a.* from (
                select BOARD_NO, TITLE, content, EMP_NO, READ_COUNT, ref, RE_STEP, RE_LVL, IN_DATE 
                from board
                where re_step = 0 
                order by board_no desc, RE_STEP ASC ) a
            ) where rn between #{start} and #{end}
