mapper.select:
SELECT * FROM (
        SELECT rownum rw , board.* FROM (
            SELECT board.* , dept.dept_name FROM BOARD 
            LEFT JOIN EMP ON board.board_reg_code = emp.emp_code
            LEFT JOIN DEPT ON emp.emp_dept_code = dept.dept_code
            order by board_code desc
        ) board
) WHERE rw BETWEEN #{start} and #{end}

mapper.update:
UPDATE BOARD
SET READ_COUNT = READ_COUNT + 1
WHERE BOARD_CODE = #{board_code}
AND REF = #{board_code}

mapper.select:
	SELECT board.* , dept.dept_name 
	FROM BOARD 
        LEFT JOIN EMP ON board.board_reg_code = emp.emp_code
        LEFT JOIN DEPT ON emp.emp_dept_code = dept.dept_code
	WHERE board.BOARD_CODE = #{board_code}
	AND board.ref = #{board_code}

mapper.update:
UPDATE BOARD
SET board_title = #{board_title} , board_contents = #{board_contents}
WHERE board_code = #{board_code}
AND   ref = #{board_code}
