mapper.insert:

    SELECT PROVIDE_SEQ.NEXTVAL FROM DUAL 


insert into provide
(
	 provide_code
	,product_won_code
	,provide_client_code
	,provide_amount
	,current_danga
	,provide_isdel
	,provide_reg_date
)
values
(
	 #{provide_code}
	,${product_won_code}
	,${provide_client_code}
	,${provide_amount}
	,${current_danga}
	,${provide_isdel}
	,sysdate
)

mapper.update:
	update provide
    set provide_isdel = #{provide_isdel}
    where provide_code = #{provide_code}

mapper.select:
	select 	*
	from 	provide
  	where 	product_won_code = #{product_won_code}
	and 	provide_client_code = #{provide_client_code}
